import React, { useRef, useEffect, useState } from 'react';
import axios from "axios"
import "../../assets/styles/MainPage.css"


function MainPage() {
  const mapContainer = useRef(null);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    // 백엔드에서 데이터 가져오기
    axios.get('http://localhost:4040/api/v1/getjson/getplace')
      .then(response => {
        console.log('Fetched Data:', response.data);  // 데이터 확인
        setLocations(response.data);
      })
      .catch(error => console.error('Error fetching locations:', error));
  }, []);

  useEffect(() => {
    if (locations.length > 0 && window.naver) {
      const { naver } = window;

      const initialLocation = new naver.maps.LatLng(38.0431160103, 128.2067618712);
      const options = {
        center: initialLocation,
        zoom: 8, // 적절한 줌 레벨로 조정
      };
      const map = new naver.maps.Map(mapContainer.current, options);


      locations.forEach(location => {
        const markerPosition = new naver.maps.LatLng(location.mapy, location.mapx);
        new naver.maps.Marker({
          position: markerPosition,
          map,
          title: location.placeTitle,
        });
      });
    }
  }, [locations]);

  function category() {
    // 카테고리 버튼 클릭 시 동작을 여기에 작성하세요.
    console.log("카테고리 버튼 클릭됨");
  }

  return (
    <div className="map-container">
      <div ref={mapContainer} className="map"></div>
      <div className="button-container">
        <button onClick={category} className="button">액티비티</button>
        <button onClick={category} className="button">식당</button>
        <button onClick={category} className="button">카페</button>
        <button onClick={category} className="button">관광지</button>
        <button onClick={category} className="button">병원</button>
        {/* 필요한 만큼 버튼을 추가하세요 */}
      </div>
    </div>
  );
}

export default MainPage;
