import React, { useRef, useEffect } from 'react';


function MainPage() {
  const mapContainer = useRef(null);

  useEffect(() => {
    //네이버 맵 호출
    const { naver } = window;

    const location = new naver.maps.LatLng(37.9, 128.3333);
    const options = {
      center: location,
      zoom: 10,
    }
    const map = new naver.maps.Map(mapContainer.current, options);

    //마커 표시

    const markerPosition = new naver.maps.LatLng(38.080402, 128.669432);
    new naver.maps.Marker({
      position : markerPosition,
      map,
    });

    const markerPosition2 = new naver.maps.LatLng(37.88888, 127.99999);
    new naver.maps.Marker({
      position : markerPosition2,
      map,
    });

  },[]);

  return (
      <div ref = {mapContainer} style={{ width: '500px', height:'500px', margin:'0 auto'}}/>
  );
}

export default MainPage;
