import React, { useState } from 'react';
import "../../assets/styles/MyPlace.css"
import axios from 'axios';

const MyPlace = () => {
    const [images, setImages] = useState([]);

    const category = (value) => {
        axios.post('http://localhost:4040/api/v1/getmycourse/getplacecat', {
          placeCat: value
        })
        .then(response => {
            console.log(response.data);
             const imageArray = response.data.map(imgData => `data:image/jpeg;base64,${imgData.firstImage2}`);
            setImages(imageArray);

        })
        .catch(error => {
          console.error('There was an error!', error);
        });
    }

    return (
        <div className="MyPlace">
            <div className="MyPlaceList">
                <h2>찜 리스트</h2>

                <div className="Category">
                    <button onClick={() => category('activity')} className="button"><span>액티비티</span></button>
                    <button onClick={() => category('restaurant')} className="button"><span>식당</span></button>
                    <button onClick={() => category('cafe')} className="button"><span>카페</span></button>
                    <button onClick={() => category('tour')} className="button"><span>관광지</span></button>
                    <button onClick={() => category('accommodation')} className="button"><span>숙박</span></button>
                </div>

                <div className="ImageList">
                    {images.map((image, index) => (
                        <img key={index} src={image} alt={`Category ${index}`} />
                    ))}
                </div>


            </div>

        
        
            <div className="TourCombination">
                <h2>관광지 조합</h2>
                <div className="tour-combination-list">
                {['액티비티', '관광지', '식당', '카페'].map((category, index) => (
                    <div className="tour-item" key={index}>
                    <img src="/path/to/your/tour/image" alt={category} />
                    </div>
                ))}
                </div>
            </div>

        </div>
    );
}

export default MyPlace;
